import React, { useState } from "react";
import axios from "axios";
import Navbar from "./navbar";
import Footer from "./footer";
import '../assets/make.css';

const Make = () => {
    const [submitted, setSubmitted] = useState(false);
    const [formData, setFormData] = useState({
        title: "",
        date: "",
        name: "",
        q1: "",
        q2: "",
        q3: "",
        q4: "",
        q5: "",
        q6: "",
        q7: 0,
        comments: "",
    });
    const [showStudentModal, setShowStudentModal] = useState(false);
    const [showTourModal, setShowTourModal] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post("http://127.0.0.1:8000/api/make_review/", formData);
            setSubmitted(true);

            setFormData({
                title: "",
                date: "",
                name: "",
                q1: "",
                q2: "",
                q3: "",
                q4: "",
                q5: "",
                q6: "",
                q7: 0,
                comments: "",
            });

            setShowStudentModal(false);
            setShowTourModal(false);
        } catch (error) {
            console.error("Error submitting review:", error);
        }
    };

    const closeModal = (modalType) => {
        if (modalType === "Student Review") {
            setShowStudentModal(false);
        } else if (modalType === "Tour Review") {
            setShowTourModal(false);
        }

        setFormData({ ...formData, q1: "" });
    };

    const openModal = (type) => {
        if (type === "Student Review") {
            setShowStudentModal(true);
        } else if (type === "Tour Review") {
            setShowTourModal(true);
        }

        setFormData({ ...formData, q1: type });
    };

    const studentModalContent = (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={() => closeModal("Student Review")}>&times;</span>
                <form onSubmit={handleSubmit}>
                    {/* ... (same as your StudentReviewModal content) */}
                </form>
            </div>
        </div>
    );

    const tourModalContent = (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={() => closeModal("Tour Review")}>&times;</span>
                <form onSubmit={handleSubmit}>
                    {/* ... (same as your TourReviewModal content) */}
                </form>
            </div>
        </div>
    );

    return (
        <div className="Reviews">
            <header>
                <Navbar />
                <div className="jumbo">
                    <h1>EXPERIENCE Geneva</h1>
                </div>
            </header>
            <main>
                <div className="subtitle">
                    <h2>Leave a Review!</h2>
                    <h5>Spread the word!</h5>
                </div>
                {submitted && <div className="success-message">Review submitted successfully!</div>}
                <div className="makeBut">
                    <p id="revDesc">Embarking on the journey of higher education is a significant decision, and Geneva College stands out as an exceptional institution for those seeking an enriching academic experience. Prospective students are encouraged to explore the unique offerings and vibrant community that define Geneva. By sharing your thoughts and experiences through a review, you not only contribute to the collective knowledge of the college but also provide invaluable insights for future students. Highlight the aspects that resonate with you, whether it's the dedicated faculty, diverse academic programs, or the strong sense of community. Your review becomes a beacon for those considering Geneva, guiding them towards a place where they can cultivate their passions, forge lasting connections, and thrive academically. Your perspective matters, and by sharing it, you play a crucial role in shaping the future of Geneva College for generations to come.</p>
                    <button id="stud" className="btn btn-lg bg-secondary text-warning btn-outline-warning" onClick={() => openModal("Student Review")}>
                        Review as a Student
                    </button>
                    <br />
                    <button id="tour" className="btn btn-lg bg-secondary text-warning btn btn-outline-warning" onClick={() => openModal("Tour Review")}>
                        Review a Tour
                    </button>
                </div>
                {showStudentModal && studentModalContent}
                {showTourModal && tourModalContent}
            </main>
            <footer>
                <Footer />
            </footer>
        </div>
    );
};

export default Make;
