import React, { useState } from "react";
import axios from "axios";
import Navbar from "./navbar";
import Footer from "./footer";
import { Button, Modal, Form } from "react-bootstrap";
import '../assets/make.css';

const Make = () => {
    const [submitted, setSubmitted] = useState(false);
    const [formData, setFormData] = useState({
        title: "",
        date: "",
        q1: "", // Changed q1 to a string type
        q2: 0,
        q3: 0,
        q4: 0,
        q5: 0,
        q6: 0,
        q7: 0,
        q8: 0,
        q9: 0,
        q10: 0,
        comments: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            await axios.post("http://mrhaydenn.us.to/api/make_review/", formData);
            setSubmitted(true);

            setFormData({
                title: "",
                date: "",
                q1: "",
                q2: 0,
                q3: 0,
                q4: 0,
                q5: 0,
                q6: 0,
                q7: 0,
                q8: 0,
                q9: 0,
                q10: 0,
                comments: "",
            });
        } catch (error) {
            console.error("Error submitting review:", error);
        }
    };

    const StudentReviewModal = () => (
        <Modal show={formData.q1 === "Review from Student"} onHide={() => setFormData({ ...formData, q1: "" })}>
            <Modal.Header closeButton>
                <Modal.Title>Student Review Form</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form.Group controlId="formQ2">
                    <Form.Label>Question 2</Form.Label>
                    <Form.Control
                        type="number"
                        name="q2"
                        value={formData.q2}
                        onChange={handleChange}
                        min="0"
                        max="10"
                    />
                </Form.Group>
                {/* Add similar form fields for other questions */}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={() => setFormData({ ...formData, q1: "" })}>
                    Close
                </Button>
                <Button variant="primary" type="submit" onClick={handleSubmit}>
                    Submit
                </Button>
            </Modal.Footer>
        </Modal>
    );

    const TourReviewModal = () => (
        <Modal show={formData.q1 === "Tour Review"} onHide={() => setFormData({ ...formData, q1: "" })}>
            <Modal.Header closeButton>
                <Modal.Title>Tour Review Form</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form.Group controlId="formQ2">
                    <Form.Label>Question 2</Form.Label>
                    <Form.Control
                        type="number"
                        name="q2"
                        value={formData.q2}
                        onChange={handleChange}
                        min="0"
                        max="10"
                    />
                </Form.Group>
                {/* Add similar form fields for other questions */}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={() => setFormData({ ...formData, q1: "" })}>
                    Close
                </Button>
                <Button variant="primary" type="submit" onClick={handleSubmit}>
                    Submit
                </Button>
            </Modal.Footer>
        </Modal>
    );

    return (
        <div className="Reviews">
            <header>
                <Navbar />
                <div className="jumbo">
                    <h1>EXPERIENCE Geneva</h1>
                </div>
            </header>
            <main>
                <div className="subtitle">
                    <h2>Make Review</h2>
                </div>
                {submitted && <div className="success-message">Review submitted successfully!</div>}
                <div>
                    <Button variant="primary" onClick={() => setFormData({ ...formData, q1: "Review from Student" })}>
                        Review as a Student
                    </Button>
                    <Button variant="primary" onClick={() => setFormData({ ...formData, q1: "Tour Review" })}>
                        Review a Tour
                    </Button>
                </div>
                <Form onSubmit={handleSubmit}>
                    <label>
                        Review Title:<br />
                        <input
                            type="text"
                            name="title"
                            value={formData.title}
                            onChange={handleChange}
                        />
                    </label>
                    <br />
                    <label>Date:<br /></label>
                    <input
                        type="date"
                        name="date"
                        value={formData.date}
                        onChange={handleChange}
                    />
                    <br />
                    <br />
                    <label>
                        Comments:<br />
                        <textarea
                            name="comments"
                            value={formData.comments}
                            onChange={handleChange}
                        />
                    </label>
                    <br />
                    <StudentReviewModal />
                    <TourReviewModal />
                </Form>
            </main>
            <footer>
                <Footer />
            </footer>
        </div>
    );
};

export default Make;
