import { NavLink } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import '../assets/navbar.css';
import Logo from '../assets/images/genevalogo.svg';
import Logo1 from '../assets/images/profile-icon.png';
import axios from 'axios';

const Navbar = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isMobile, setIsMobile] = useState(false);
    const [isHamburgerOpen, setIsHamburgerOpen] = useState(false);
    const axiosInstance = axios.create({
        baseURL: 'http://mrhaydenn.us.to:8000',
    });

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 768); // Adjust the width as per your requirement
        };

        handleResize();

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const handleLogin = () => {
        if (username && password) {
            const data = {
                username: username,
                password: password,
            };

            axiosInstance.post('http://mrhaydenn.us.to:8000/api/login/', data)
                .then(response => {
                    console.log(response.data);
                    localStorage.setItem('token', response.data.token);
                    setIsLoggedIn(true);
                })
                .catch(error => {
                    console.error(error);
                });
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        setUsername('');
        setPassword('');
        setIsLoggedIn(false);
    };

    const toggleHamburger = () => {
        setIsHamburgerOpen(!isHamburgerOpen);
    };

    return (
        <nav className="navbar">
            <div className="container">
                <div className="nav-elements">
                    <img src={Logo} className="genLogo" alt="geneva logo" />
                    {isMobile ? (
                        <>
                            <input id="myBtn" className="buttonImg" type="Image" src={Logo1} height="50" width="50" name="Icon" onClick={toggleHamburger} />
                            {isHamburgerOpen && (
                                <ul className="navList">
                                    <li>
                                        <NavLink to="/" onClick={toggleHamburger}>Home</NavLink>
                                    </li>
                                    <li>
                                        <NavLink to="/events" onClick={toggleHamburger}>Events</NavLink>
                                    </li>
                                    <li>
                                        <NavLink to="/trivia" onClick={toggleHamburger}>Trivia</NavLink>
                                    </li>
                                    <li>
                                        <NavLink to="/upload" onClick={toggleHamburger}>Upload</NavLink>
                                    </li>
                                    <li>
                                        <NavLink to="/reviews" onClick={toggleHamburger}>Reviews</NavLink>
                                    </li>
                                </ul>
                            )}
                        </>
                    ) : (
                        <ul className="navList">
                            <li>
                                <NavLink to="/">Home</NavLink>
                            </li>
                            <li>
                                <NavLink to="/events">Events</NavLink>
                            </li>
                            <li>
                                <NavLink to="/trivia">Trivia</NavLink>
                            </li>
                            <li>
                                <NavLink to="/upload">Upload</NavLink>
                            </li>
                            <li>
                                <NavLink to="/reviews">Reviews</NavLink>
                            </li>
                        </ul>
                    )}
                    <div id="myModal" className="modal">
                        <div className="modal-content">
                            <span className="close">&times;</span>
                            {isLoggedIn ? (
                                <div>
                                    <h3>Welcome, {username}!</h3>
                                    <button onClick={() => handleLogout()}>Logout</button>
                                </div>
                            ) : (
                                <div>
                                    <h3>Login</h3>
                                    <p>Username:</p>
                                    <input
                                        type="text"
                                        value={username}
                                        onChange={(e) => setUsername(e.target.value)}
                                    />
                                    <p>Password:</p>
                                    <input
                                        type="password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                    />
                                    <button onClick={() => handleLogin()}>Login</button>
                                    <a href='/register' target="_blank" rel="noopener noreferrer">
                                        <button className="register-button">Create Account</button>
                                    </a>

                                </div>
                            )}
                            <NavLink to="/profile">Go To Profile (temporary)</NavLink>
                        </div>
                    </div>
                </div>
            </div>
        </nav>
    );
}

export default Navbar;
