import React, { useState, useEffect } from "react";
import Navbar from "./navbar";
import Footer from "./footer";
import { NavLink } from 'react-router-dom';
import axios from 'axios';

const Reviews = () => {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {

        const fetchReviews = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/get_reviews/');
                setReviews(response.data);
            } catch (error) {
                console.error('Error fetching reviews:', error);
            }
        };

        fetchReviews();
    }, []);

    return (
        <div className="Reviews">
            <header>
                <Navbar />
                <div className="jumbo">
                    <h1>EXPERIENCE Geneva</h1>
                </div>
            </header>
            <main>
                <div className="subtitle">
                    <h2>Reviews</h2>
                </div>
                <NavLink to="/make">
                    <button className="make-button">Leave a Review!</button>
                </NavLink>

                {reviews.map((review) => (
                    <div key={review.title}>
                        <h3>{review.title}</h3>
                        <p> Date:{review.date}</p>
                        <p> From 1-10, Rate the Tour Guide's Knowledge:{review.q1}</p>
                        <p> From 1-10, Rate the Tour Guide & Faculty Engagement Level:{review.q2}</p>
                        <p> From 1-10, Rate Our Campus Facilities: {review.q3}</p>
                        <p>From 1-10, Rate the Tour Organization and Structure:{review.q4}</p>
                        <p>From 1-10, Rate the Campus Accessibility:{review.q5}</p>
                        <p>From 1-10, Rate the Interactive Elements:{review.q6}</p>
                        <p>From 1-10, Rate the Tour Duration:{review.q7}</p>
                        <p>From 1-10, Rate Your Dining Experience:{review.q8}</p>
                        <p>From 1-10, Rate Your Overall Satisfaction:{review.q9}</p>


                    </div>
                ))}
            </main>
            <footer>
                <Footer />
            </footer>
        </div>
    );
};

export default Reviews;
