import React, { useState } from "react";
import Navbar from "./navbar";
import Footer from "./footer";
import "../assets/trivia.css";
import Logo from "../assets/images/trivia_image.jpg";

const Trivia = () => {
    const questions = [
        {
            question: "In what year was Geneva College founded?",
            options: ["1848", "1870", "1902", "1925"],
            correctAnswer: "1848",
        },
        {
            question: "Which department offers the Computer Science major at Geneva College?",
            options: ["Mathematics", "Business", "Computer Science", "Psychology"],
            correctAnswer: "Computer Science",
        },
        {
            question: "What is the official mascot of Geneva College?",
            options: ["Eagle", "Lion", "Bear", "Phoenix"],
            correctAnswer: "Golden Tornado",
        },
        {
            question: "Which building serves as the iconic centerpiece on Geneva College's campus?",
            options: ["Old Main", "Library", "Science Building", "Athletic Center"],
            correctAnswer: "Old Main",
        },
        {
            question: "In which state is Geneva College located?",
            options: ["Ohio", "New York", "Pennsylvania", "Indiana"],
            correctAnswer: "Pennsylvania",
        },
        // Add more questions as needed
    ];

    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [userAnswers, setUserAnswers] = useState(Array(questions.length).fill(null));
    const [score, setScore] = useState(0);

    const handleAnswer = (answer) => {
        const newAnswers = [...userAnswers];
        newAnswers[currentQuestion] = answer;
        setUserAnswers(newAnswers);
    };

    const handleNextQuestion = () => {
        setCurrentQuestion((prevQuestion) => prevQuestion + 1);
    };

    const handleComplete = () => {
        // Calculate the score based on correct answers
        const newScore = userAnswers.reduce((acc, userAnswer, index) => {
            if (userAnswer === questions[index].correctAnswer) {
                return acc + 1;
            }
            return acc;
        }, 0);
        setScore(newScore);
    };

    const printQuestions = () => {
        alert(
            "Geneva College Trivia Questions:\n\n" +
            questions
                .map(
                    (q, index) =>
                        `\nQuestion #${index + 1}: ${q.question}\n` +
                        q.options.map((option, optionIndex) => `${String.fromCharCode(65 + optionIndex)}. ${option}`).join("\n") +
                        `\nCorrect Answer: ${q.correctAnswer}`
                )
                .join("\n")
        );
    };

    return (
        <div className="Trivia">
            <header>
                <Navbar />
                <div className="jumbo">
                    <h1>EXPERIENCE Geneva</h1>
                </div>
                <div className="subtitle">
                    <h2>Trivia</h2>
                </div>
            </header>

            <main>
                <h1 className="title">Weekly Geneva Trivia</h1>
                <div className="imgMove">
                    <img src={Logo} className="triviaImage" alt="Trivia Logo" />
                </div>
                <div className="startButton">
                    <button onClick={() => handleNextQuestion()}>Start</button>
                </div>
                <button onClick={() => handleComplete()} disabled={currentQuestion !== questions.length - 1}>
                    Complete
                </button>
                <button onClick={() => printQuestions()}>Display Questions</button>

                <div className="questions">
                    <h4>{`Question #${currentQuestion + 1}: ${questions[currentQuestion].question}`}</h4>
                    {questions[currentQuestion].options.map((option, index) => (
                        <div key={index}>
                            <input
                                type="radio"
                                id={index}
                                name={`question${currentQuestion}`}
                                value={option}
                                checked={userAnswers[currentQuestion] === option}
                                onChange={() => handleAnswer(option)}
                            />
                            <label htmlFor={index}>{option}</label>
                        </div>
                    ))}
                </div>

                <div>
                    <button onClick={() => handleNextQuestion()} disabled={currentQuestion === questions.length - 1}>
                        Next Question
                    </button>
                </div>

                <div className="footer">
                    <p>
                        Current Score: {score}
                        <span>All Time High Score: (update this with your high score)</span>
                    </p>
                </div>
            </main>
            <footer>
                <Footer />
            </footer>
        </div>
    );
};

export default Trivia;
