import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import '../assets/navbar.css';
import Logo from '../assets/images/genevalogo.svg';
import Logo1 from '../assets/images/profile-icon.png';
import Register from './Register'; // Import the Register component

const Navbar = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegisterOpen, setIsRegisterOpen] = useState(false); // Add state for the registration component

    const handleLogin = () => {
        if (username && password) {
            setIsLoggedIn(true);
        }
    };

    const handleLogout = () => {
        setUsername('');
        setPassword('');
        setIsLoggedIn(false);
    };

    const openRegister = () => {
        setIsRegisterOpen(true);
    }

    return (
        <nav className="navbar">
            <div className="container">
                <div className="nav-elements">
                    <ul className="navList">
                        {/* ... (your existing navigation links) ... */}
                        <button onClick={openRegister}>Create Account</button> {/* Open the registration component */}
                    </ul>
                    <input id="myBtn" className="buttonImg" type="Image" src={Logo1} height="50" width="50" name="Icon"></input>
                </div>
            </div>
            {isRegisterOpen && <Register />} {/* Display the registration component */}
        </nav>
    );
}

export default Navbar;
