import React, { useState, useEffect } from "react";
import Navbar from "./navbar";
import Footer from "./footer";
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import '../assets/review.css';

const Reviews = () => {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {

        const fetchReviews = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/get_reviews/');
                setReviews(response.data);
            } catch (error) {
                console.error('Error fetching reviews:', error);
            }
        };

        fetchReviews();
    }, []);

    return (
        <div className="Reviews">
            <header>
                <Navbar />
                <div className="jumbo">
                    <h1>EXPERIENCE Geneva</h1>
                </div>
            </header>
            <main>
                <div className="subtitle">
                    <h2>Reviews</h2>
                    <NavLink to="/make">
                        <button className="make-button">Leave a Review!</button>
                    </NavLink>
                </div>
                {reviews.map((review, index) => (
                    <div className="reviewFeed" key={index}>
                        <h3>{review.title}</h3>
                        <p>Date: {review.date}</p>
                        {/* Add the rest of your review content here */}
                    </div>
                ))}
            </main>
            <footer>
                <Footer />
            </footer>
        </div>
    );
};

export default Reviews;
