import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import '../assets/navbar.css';
import Logo from '../assets/images/genevalogo.svg';
import Logo1 from '../assets/images/profile-icon.png';
import axios from 'axios';

const Navbar = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegisterModalOpen, setIsRegisterModalOpen] = useState(false); // Add state for the registration modal

    const handleLogin = () => {
        if (username && password) {
            setIsLoggedIn(true);
        }
    };

    const handleLogout = () => {
        setUsername('');
        setPassword('');
        setIsLoggedIn(false);
    };

    useEffect(() => {
        var modal = document.getElementById("myModal");
        var btn = document.getElementById("myBtn");
        var span = document.querySelector(".close");

        if (btn && modal && span) {
            btn.onclick = function() {
                modal.style.display = "block";
            }

            span.onclick = function() {
                modal.style.display = "none";
                setIsRegisterModalOpen(false); // Close the registration modal
            }

            window.onclick = function(event) {
                if (event.target == modal) {
                    modal.style.display = "none";
                    setIsRegisterModalOpen(false); // Close the registration modal
                }
            }
        }
    }, []);

    const openRegisterModal = () => {
        setIsRegisterModalOpen(true);
    }

    return (
        <nav className="navbar">
            <div className="container">
                <div className="nav-elements">
                    <ul className="navList">
                        {/* ... (your existing navigation links) ... */}
                        <button onClick={openRegisterModal}>Create Account</button> {/* Open the registration modal */}
                    </ul>
                    <input id="myBtn" className="buttonImg" type="Image" src={Logo1} height="50" width="50" name="Icon"></input>
                    <div id="myModal" className="modal">
                        <div className="modal-content">
                            <span className="close" onClick={() => handleLogout()}>&times;</span>
                            {isLoggedIn ? (
                                <div>
                                    <h3>Welcome, {username}!</h3>
                                    <button onClick={() => handleLogout()}>Logout</button>
                                </div>
                            ) : (
                                <div>
                                    <h3>Login</h3>
                                    <p>Username:</p>
                                    <input
                                        type="text"
                                        value={username}
                                        onChange={(e) => setUsername(e.target.value)}
                                    />
                                    <p>Password:</p>
                                    <input
                                        type="password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                    />
                                    <button onClick={() => handleLogin()}>Login</button>
                                    <button onClick={openRegisterModal}>Create Account</button>
                                </div>
                            )}
                            <NavLink to="/profile">Go To Profile (temporary)</NavLink>
                        </div>
                    </div>
                </div>
            </div>
            {isRegisterModalOpen && <Register />} {/* Display the registration modal */}
        </nav>
    );
}

export default Navbar;
