import React, { useState } from "react";
import axios from "axios";
import Navbar from "./navbar";
import Footer from "./footer";

const Make = () => {
    const [submitted, setSubmitted] = useState(false);
    const [formData, setFormData] = useState({
        title: "",
        date: "",
        q1: 0,
        q2: 0,
        q3: 0,
        q4: 0,
        q5: 0,
        q6: 0,
        q7: 0,
        q8: 0,
        q9: 0,
        q10: 0,
        comments: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            await axios.post("http://127.0.0.1:8000/api/make_review/", formData);
            setSubmitted(true);

            setFormData({
                title: "",
                date: "",
                q1: 0,
                q2: 0,
                q3: 0,
                q4: 0,
                q5: 0,
                q6: 0,
                q7: 0,
                q8: 0,
                q9: 0,
                q10: 0,
                comments: "",
            });

        } catch (error) {
            console.error("Error submitting review:", error);
        }
    };

    return (
        <div className="Reviews">
            <header>
                <Navbar />
                <div className="jumbo">
                    <h1>EXPERIENCE Geneva</h1>
                </div>
            </header>
            <main>
                <div className="subtitle">
                    <h2>Make Review</h2>
                </div>
                {submitted && <div className="success-message">Review submitted successfully!</div>}
                <form onSubmit={handleSubmit}>

                    <label>
                        Review Title:
                        <input
                            type="text"
                            name="title"
                            value={formData.title}
                            onChange={handleChange}
                        />
                    </label>
                    <br />
                    <label>
                        Date of Tour:
                        <input
                            type="text"
                            name="date"
                            value={formData.date}
                            onChange={handleChange}
                        />
                    </label>
                    <label>
                        From 1-5, how was your tour guide?:
                        <input
                            type="number"
                            name="q1"
                            value={formData.q1}
                            onChange={handleChange}
                        />
                    </label>
                    <br />
                    {[...Array(10)].map((_, index) => (
                        <div key={index}>
                            <label>
                                Question {index + 1}:
                                <input
                                    type="number"
                                    name={`q${index + 1}`}
                                    value={formData[`q${index + 1}`]}
                                    onChange={handleChange}
                                />
                            </label>
                            <br />
                        </div>
                    ))}

                    <label>
                        Comments:
                        <textarea
                            name="comments"
                            value={formData.comments}
                            onChange={handleChange}
                        />
                    </label>
                    <br />

                    <button type="submit">Submit</button>
                </form>
            </main>
            <footer>
                <Footer />
            </footer>
        </div>
    );
};

export default Make;
